    CUTSCENES = [
        Msn8Offer,
        Msn8Osiris,
    ]
    SPACE_CLASS = Msn8Space
    SYNC_SPACE = True

    MISSION_TITLE = 'Миссия 8. Эксперт по Кочевникам'
